def notas():    """    Essa função pega notas de alunos e analisa elas, mostrando total de notas, a quantidade de notas,    a maior nota, a menor nota, a media e a situação da turma (opcional.)    """    notas = {}    contador = 0    while True:        contador += 1        notas[f'Aluno{contador}'] = int(input(f'Qual é a nota do {contador} aluno?: '))        continuar = input('Quer adicionar mais alguma nota?: ')        while continuar not in 'SsNn':            continuar = input('ERRO!Quer adicionar mais alguma nota?: ')        if continuar in 'Nn':            situacao = input('Quer saber a situação da turma?: ')            media = sum(notas.values()) / contador            print(f'Essas foram todas as notas {notas}')            print(f'''A quatidade de notas foi {len(notas)} a maior nota foi {max(notas.values())}A menor foi {min(notas.values())} e media da turma é {media:.2f}''')            if situacao in 'Ss':                if media < 5:                    print('A situação da turma é ruim!')                elif 5 < media < 7:                    print('A situação da turma é boa!')                elif media >= 8:                    print('A situação da turma é exelente!')            breaknotas()